def.body:
if new_name.empty? || (new_name =~ (/\W/))
  raise(EMail::Error::ClientConfigError.new("Invalid client name \"#{new_name}\""))
end
@client_name = new_name

{% if flag?(:without_openssl) %}
        raise EMail::Error::ClientConfigError.new("TLS is disabled because `-D without_openssl` was passed at compile time")
      {% end %}
@tls = tls_mode

if @body_resources.has_key?(cid)
  raise(EMail::Error::MessageError.new("CID #{cid} already exists."))
end
@body_resources[cid] = EMail::Content::AttachmentFile.new(file_path, file_id: cid, file_name: file_name, mime_type: mime_type)

if @body_resources.has_key?(cid)
  raise(EMail::Error::MessageError.new("CID #{cid} already exists."))
end
@body_resources[cid] = EMail::Content::AttachmentFile.new(io, file_id: cid, file_name: file_name, mime_type: mime_type)
